graphql.query:
query provincesOfCountry(
    $criteria: ProvinceCriteria
    $noCode: Boolean! = true
    $detailed: Boolean!
) {
    country(criteria: $criteria) {
        code @skip(if: $noCode)
        name
        population @include(if: $detailed)
        area
        capital {
            ...cityData
        }
        regions: provinces {
            name
            capital {
                name
                population @include(if: $detailed)
            }
            population @include(if: $detailed)
            area
        }
    }
}

fragment cityData on City {
    name
    population
    elevation
    province {
        name
        population
    }
}

