query introspection {
    __schema {
        description
        types {
            name
            kind
            description
            fields {
                ...fieldData
            }
            interfaces {
                name
                kind
            }
            enumValues {
                ...enumData
            }
        }
    }
}

fragment fieldData on __Field {
    name
    description
    args {
        name
        description
        type {
            kind
            name
        }
        defaultValue
    }
    type {
        ...fieldTypeData
    }
    isDeprecated
    deprecationReason
}

fragment fieldTypeData on __Type {
    name
    kind
    ofType {
        name
        kind
        ofType {
            name
            kind
        }
    }
}

fragment enumData on __EnumValue {
    name
    description
    isDeprecated
    deprecationReason
}

query introspectionPlace {
    __type(name: "Place") {
        name
        kind
        fields(includeDeprecated: true) {
            name
            description
            type {
                ...unwrapType
            }
        }
        ofType {
            name
            description
            __typename
        }
        possibleTypes {
            name
            __typename 
        }
    }
}

fragment unwrapType on __Type {
    name
    ofType {
        name
        ofType {
            name
        }
    }
}

